public class GCD {

    public static int getGCD(int x, int y) { 
        // Base case 
        if (x == 0) 
            return y; 
        if (y == 0) 
            return x; 

        // If x is greater 
        if (x > y) 
            return getGCD(x%y, y); 

        return getGCD(x, y%x); 
    } 

    public static void main(String[] args) { 
        int x = 40; 
        int y = 20; 
        System.out.println("GCD of " + x +" and " + y + " is " + getGCD(x, y)); 
    } 
}

