function checkAnagram(string1, string2) {
  if(string1.length !== string2.length) {
    return false;
  }
  var charCounter1 = {}, charCounter2 = {};

  for(var  i = 0; i < string1.length; i++) {
    var char1 = string1[i];
    var char2 = string2[i];

    if(charCounter1[char1]) {
      charCounter1[char1]++;
    } else {
      charCounter1[char1] = 1;
    }

    if(charCounter2[char2]) {
      charCounter2[char2]++;
    } else {
      charCounter2[char2] = 1;
    }
  }

  for(var char in charCounter1) {
    if(charCounter1[char] !== charCounter2[char]) {
      return false;
    }
  }

  return true;
}

